generate_paths = 
  let
    f graph = 
      case graph of
        Gnil _ -> Pcons (let
            tmp1 =
              ((Nil Unit), (Nil Unit))
          in
          evalU1_0 (RefEnv []) ((genSym (1::Int) "hole1") :: (UnionM Expr1_0))) (Pnil Unit)
        Gcons edges remains -> 
          let
